import Image from 'next/image'

type ImageProps = {
    src: string | any
    alt: string
    width: number
    height: number
    className?: string
    style?: React.CSSProperties,
    onClick?: any
}

export const TodaiImage = ({
    src,
    alt,
    className,
    width,
    height,
    style,
    onClick
}: ImageProps) => {
    return (
        <Image
            src={src}
            className={className}
            width={width}
            height={height}
            style={style}
            alt={alt || 'Todai Image'}
            onClick={onClick}
            sizes="(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 33vw"
            quality={75}
            // placeholder='blur'
            //blurDataURL='data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNkqPy/HwAEMgI5EwqKeAAAAABJRU5ErkJggg=='
            // blurDataURL='data:image/svg+xml;base64,'
            loading='lazy'
        />
    )
}
